import React from "react";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { faPenToSquare } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import axios from "axios";
import "./CSS/AdminHome.css";



const AdminHome = ({ privateData, username, email, users, logoutHandler }) => {
  const deleteUser = async (id, username) => {
    if (window.confirm(`Are you sure you want to delete ${username}`)) {
      try {
        const response = await axios.delete("/deleteUser",{data: { userid: id }});
        if (response.data.status === "ok") {
          // Reload the users list after successful deletion
          window.location.reload();
        }
      } catch (error) {
        console.log(error);
      }
    } else {
    }
  };
  return (
    <div >
      <div style={{ background: "green", color: "white" }}>{privateData}</div>
      <div className="whole-page">
        <h2>Welcome, {username}!</h2>
        <h3>Your email is: {email}</h3>
        <table className="custom-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Username</th>
              <th>Email</th>
              <th>User Type</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td>{user._id}</td>
                <td>{user.username}</td>
                <td>{user.email}</td>
                <td>{user.userType}</td>
                <td>
                  <FontAwesomeIcon className="mx-4"
                    icon={faTrash}
                    onClick={() => deleteUser(user._id, user.username)}
                  />
                  <FontAwesomeIcon 
                  icon={faPenToSquare}
                  onClick={() => editUser(user._id, user.username)} />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <button className="btn btn-primary logout-btn" onClick={logoutHandler}>Logout</button>{' '}

      </div>
    </div>
  );
};

export default AdminHome;
