import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const PrivateScreen = () => {
  const [error, setError] = useState("");
  const [privateData, setPrivateData] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [users, setProducts] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {
    if (!localStorage.getItem("authToken")) {
      navigate("/login");
    }
    const fetchPrivateData = async () => {
      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("authToken")}`,
        },
      };

      try {
        const { data } = await axios.get("/api/private", config);
        setPrivateData(data.data);

        // 3- Retrieve the username from local storage
        const storedUsername = localStorage.getItem("username");
        setUsername(storedUsername);

        const storedemail = localStorage.getItem("email");
        setEmail(storedemail);
      } catch (error) {
        console.log(error);
        localStorage.removeItem("authToken");
        setError("You are not authorized to login");
      }
    };
    fetchPrivateData();
  }, []);

  const logoutHandler = () => {
    localStorage.removeItem("authToken");
    navigate("/login");
  };

  useEffect(() => {
    axios.get("/users").then((res) => {
      setProducts(res.data);
    });
  }, []);

  return error ? (
    <span className="error-message">{error}</span>
  ) : (
    <>
      <div style={{ background: "green", color: "white" }}>{privateData}</div>
      <p>Welcome, {username}!</p>
      <p>Your email is: {email}</p>
      {/* JSON.stringify() is a JavaScript method used to convert a JavaScript object or value to a JSON string
       2 is used to add indentation to the resulting JSON string for better readability. */}
      {users.map((users) => (
        <pre key={users._id}>{JSON.stringify(users, null, 2)}</pre>
      ))}
      {/* Display users data in a table */}
      <table >
        <thead>
          <tr>
            <th>ID</th>
            <th>Username</th>
            <th>Email</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user._id}</td>
              <td>{user.username}</td>
              <td>{user.email}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={logoutHandler}>Logout</button>
    </>
  );
};

export default PrivateScreen;
