import { useState, useEffect } from "react";
import axios from "axios";
import { Link, Navigate } from "react-router-dom";
import "./LoginScreen.css";

const LoginScreen = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    if (localStorage.getItem("auth_token")) {
      Navigate("/");
    }
  }, []);

  const loginHandler = async (e) => {
    e.preventDefault();
    const config = {
      header: {
        "Content-Type": "application/json",
      },
    };

    // try {
    //   const { data } = await axios.post(
    //     "/api/auth/login",
    //     { email, password },
    //     config
    //   );
    //   localStorage.setItem("authToken", data.token);

    //   Navigate("/");
    // } catch (error) {
    //   setError(error.response.data.error);
    //   setTimeout(() => {
    //     setError("");
    //   }, 5000);
    // }
    try {
      const response = await axios.post("/api/auth/login", { email, password }, config);
      if (response && response.data && response.data.token) {
        localStorage.setItem("authToken", response.data.token);
        Navigate("/");
      } else {
        // Handle the case when the response doesn't have the expected data
        console.error("Unexpected response format:", response);
      }
    } catch (error) {
      setError(error.response?.data?.error || "An error occurred.");
      setTimeout(() => {
        setError("");
      }, 5000);
    }
    
  };

  return (
    <div className="login-screen">
      <form onSubmit={loginHandler} className="login-screen__form">
        <h3 className="login-screen__title">Login</h3>
        {error && <span className="error-message"> {error}</span>}

        {/* email */}
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            required
            id="email"
            placeholder="Enter email address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            tabIndex={1}
          />
        </div>

        {/* password */}
        <div className="form-group">
          <label htmlFor="password">Password:
          <Link to="/forgotpassword" className="login-screen__forgotpassword" tabIndex={4}> Forgot Password?</Link></label>
          <input
            type="password"
            required
            id="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            tabIndex={2}
          />
        </div>

        <button type="submit" className="btn btn-primary" tabIndex={3}>
          Login
        </button>
        <span className="login-screen__subtext">
          Don't have an account? <Link to="/register">Register</Link>
        </span>
      </form>
    </div>
  );
};

export default LoginScreen;
