import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

//Routing
// import PrivateRoute from "./components/routing/PrivateRoute";

//Screens
import PrivateScreen from "./components/screens/PrivateScreen";
import LoginScreen from "./components/screens/LoginScreen";
import RegisterScreen from "./components/screens/RegisterScreen";
import ForgotPasswordScreen from "./components/screens/ForgotPasswordScreen";
import ResetPasswordScreen from "./components/screens/ResetPasswordScreen";
import LoggedinScreen from "./components/screens/LoggedinScreen";
import Users from "./components/screens/Users";
import { UserDetails } from "./components/screens/UserDetails";
const App = () => {
  return (
    <Router>
      <div>
        <Routes>
          <Route path="/" element={<PrivateScreen />} />
          <Route path="/login" element={<LoginScreen />} />
          <Route path="/register" element={<RegisterScreen />} />

          <Route path="/forgotpassword" element={<ForgotPasswordScreen />} />
          <Route
            path="/passwordreset/:resetToken"
            element={<ResetPasswordScreen />}
          />
          <Route path="/users" element={<Users />} />

          {/* <Route path="/:username" element={<LoggedinScreen />} /> */}
          <Route path="/:user1" element={<UserDetails />} />
          <Route path="/:user2" element={<UserDetails />} />
          <Route path="/:user3" element={<UserDetails />} />

        </Routes>
      </div>
    </Router>
  );
};

export default App;
